INSERT INTO `craft_content` (`id`, `elementId`, `locale`, `title`, `field_pageIntro`, `field_mainCopy`, `field_projectUrl`, `field_pageCallOut`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('2', '2', 'en', 'Home', '<p>I work with organisations, individuals and agencies to build successful and user-friendly websites.</p>', NULL, NULL, '<p>Whatever your needs, I’d love to hear about your goals and ideas and help you make your website a success.</p>', '2014-05-12 11:05:30', '2014-10-14 13:42:56', '57497823-dc8a-4ed9-83ed-ec7828d7b95d');
