INSERT INTO `craft_content` (`id`, `elementId`, `locale`, `title`, `field_pageIntro`, `field_mainCopy`, `field_projectUrl`, `field_pageCallOut`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('6', '5', 'en', 'About', '<p>I am a freelancer based in Petersfield; a market down in the midst of the beautiful South Downs.</p>', NULL, NULL, '<p>I’d love to hear about your goals and ideas and help you make your website a success.</p>', '2014-05-26 15:03:37', '2014-10-14 12:53:04', 'bc288c8f-7aa7-4ca4-9bc2-d9029a7c0301');
INSERT INTO `craft_content` (`id`, `elementId`, `locale`, `title`, `field_pageIntro`, `field_mainCopy`, `field_projectUrl`, `field_pageCallOut`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('8', '7', 'en', 'Projects', '<p>Over the last five years I have worked with a variety of organisations and individuals.</p>', NULL, NULL, '<p>Think I could be the perfect fit for your project? Get in touch.</p>', '2014-05-26 15:06:11', '2014-10-14 12:35:41', '9a0f340b-627c-4d7b-9541-16998dcf29ee');
