INSERT INTO `craft_content` (`id`, `elementId`, `locale`, `title`, `field_pageIntro`, `field_mainCopy`, `field_projectUrl`, `field_pageCallOut`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('10', '40', 'en', 'Sustainable Food City', NULL, '<p>When John from&nbsp;<a href=\"http://weareabo.com/\">A/B/O</a> asked me&nbsp;to help build the website for&nbsp;Bournemouth and Poole’s&nbsp;Sustainable Food City partnership, I jumped at the chance.</p><p>The project has a fantastic cause, with local businesses, community groups and public sector organisations&nbsp;working together to positively&nbsp;change&nbsp;the way people grow, buy, cook, eat, and dispose of food.</p><p>We’ve now launched the holding page and are currently working on the main site.</p>', 'http://www.sustainablefoodcity.org/', NULL, '2014-06-26 08:48:19', '2014-10-13 22:10:20', '026c45e1-ddd3-4c20-b87e-08761bf5b1c7');
