INSERT INTO `craft_content` (`id`, `elementId`, `locale`, `title`, `field_pageIntro`, `field_mainCopy`, `field_projectUrl`, `field_pageCallOut`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('34', '116', 'en', 'Totally Thames', NULL, '<p>The Thames Festival Trust has been organising the annual Mayor’s Thames Festival and Rivers of the World project for many years. Now, in 2014, it is extending it’s annual weekend festival into a month-long series of events and celebrations.</p><p><a href=\"http://thoughtfulweb.co.uk/\">Thoughtful Web</a> was in charge&nbsp;to create the new site, which needed to merge the vast existing content with the new events and details. I joined the project to build a documented modular, responsive front-end structure, which we then integrated with&nbsp;<a href=\"http://ellislab.com/expressionengine\">ExpressionEngine</a>.</p>', 'http://totallythames.org/', NULL, '2014-07-22 14:44:39', '2014-10-13 16:56:14', '23c70e0e-b781-4858-970d-ebce7086c518');
INSERT INTO `craft_content` (`id`, `elementId`, `locale`, `title`, `field_pageIntro`, `field_mainCopy`, `field_projectUrl`, `field_pageCallOut`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('35', '120', 'en', 'hvo-main', NULL, NULL, NULL, NULL, '2014-07-23 08:10:04', '2014-08-19 14:28:33', 'b964f64d-24be-44ad-9e6d-1d3f4052d749');
INSERT INTO `craft_content` (`id`, `elementId`, `locale`, `title`, `field_pageIntro`, `field_mainCopy`, `field_projectUrl`, `field_pageCallOut`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('36', '121', 'en', 'hvo-thumb', NULL, NULL, NULL, NULL, '2014-07-23 08:10:05', '2014-08-19 14:28:34', '3bdc6d99-770d-455a-be86-276423af7d84');
INSERT INTO `craft_content` (`id`, `elementId`, `locale`, `title`, `field_pageIntro`, `field_mainCopy`, `field_projectUrl`, `field_pageCallOut`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('37', '122', 'en', 'HVO search', NULL, '<p>Maria&nbsp;needed&nbsp;an&nbsp;elegant site to sell her executive search&nbsp;services and allow her to regularly post events and news. Having been let down by another web designer and with an important event coming up, Maria was left scrambling. So we’ve pulled up our sleeves and worked hard to create a site that conveys HVO’s&nbsp;approachability&nbsp;and professionalism.</p>', 'http://hvosearch.com/', NULL, '2014-07-23 08:39:37', '2014-10-13 16:20:07', '3a6b0d9b-8bd5-4576-85bd-de19c8a9005d');
INSERT INTO `craft_content` (`id`, `elementId`, `locale`, `title`, `field_pageIntro`, `field_mainCopy`, `field_projectUrl`, `field_pageCallOut`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('41', '128', 'en', 'Urban Classic', NULL, '<p>Since 2006, the Urban Classic project has been bringing together&nbsp;urban artists with classically trained musicians, resulting in&nbsp;incredible performances.</p><p>On 10th August 2013, Urban Classic was featured in the BBC Proms at the Royal Albert Hall. Up until that point the project had no website and one was needed to&nbsp;tell its story, inform about upcoming events and introduce the artists.</p>', 'http://urbanclassicproject.com', NULL, '2014-07-23 09:17:24', '2014-10-13 18:13:32', '4e17b08b-622c-4257-8e85-1fdb80b2cddd');
INSERT INTO `craft_content` (`id`, `elementId`, `locale`, `title`, `field_pageIntro`, `field_mainCopy`, `field_projectUrl`, `field_pageCallOut`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('42', '130', 'en', 'urban-classic-main', NULL, NULL, NULL, NULL, '2014-07-23 09:53:19', '2014-08-19 14:28:36', '7be44537-1e7a-45d0-bbb8-036c9073d0f7');
