INSERT INTO `craft_content` (`id`, `elementId`, `locale`, `title`, `field_pageIntro`, `field_mainCopy`, `field_projectUrl`, `field_pageCallOut`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('44', '133', 'en', 'Shoreditch Town Hall', NULL, '<p>Shoreditch Town Hall is a stunning, unique venue in East London. It needed to refresh it’s old site to help portray it&nbsp;as a hub for artistic and cultural events, as well as a home fro private and commercial functions.</p><p><a href=\"http://thoughtfulweb.co.uk/\">Thoughtful Web</a> asked me join this project to manage the build of the new site.</p>', 'http://shoreditchtownhall.com/', NULL, '2014-07-23 18:47:03', '2014-10-13 17:56:30', '3a86cbaf-f631-4cd4-b538-a20d7d072f72');
