INSERT INTO `craft_content` (`id`, `elementId`, `locale`, `title`, `field_pageIntro`, `field_mainCopy`, `field_projectUrl`, `field_pageCallOut`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('60', '151', 'en', 'Arboreal Architecture', NULL, '<p>Arboreal needed to upgrade their old Flash website to make it accessible&nbsp;across&nbsp;devices and allow for its content to be easily updated.&nbsp;</p><p>With the designs supplied by&nbsp;Arboreal, the result is an&nbsp;elegant, fully responsive site which really&nbsp;lets their beautiful work shine and is fully content-managed through&nbsp;<a href=\"http://statamic.com/\">Statamic</a>.</p>', 'http://arborealarchitecture.com/', NULL, '2014-07-24 12:24:28', '2014-10-13 16:20:07', '2d553069-90ed-4623-b7c2-850d841dd9d9');
