INSERT INTO `craft_content` (`id`, `elementId`, `locale`, `title`, `field_pageIntro`, `field_mainCopy`, `field_projectUrl`, `field_pageCallOut`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('69', '162', 'en', 'Artichoke', NULL, '<p>The Artichoke Trust organises stunning, large-scale events and festivals across the UK.&nbsp;The website needed to communicate their stunning work through a variety of media formats, attract donations and keep users up-to-date about events.</p><p>Working at&nbsp;<a href=\"http://cogdesign.com/\">Cog Design</a>,&nbsp;I led the build of the site which is content-managed with&nbsp;<a href=\"http://ellislab.com/expressionengine\">ExpressionEngine</a>. I also built a set of easy-to-use&nbsp;<a href=\"http://www.campaignmonitor.com/\">Campaign Monitor</a> templates for regular email updates.</p>', 'http://www.artichoke.uk.com/', NULL, '2014-07-24 19:07:58', '2014-10-13 16:20:08', '2d378406-92ee-42c7-92e5-1dc71df4e97a');
