INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_sectionCodeSnippet_codeSnippet`, `field_sectionImage_imageCaption`, `field_sectionImage_imageAlt`, `field_sectionCodeSnippet_codeType`, `field_sectionImage_remoteImage`, `field_sectionQuote_quoteText`, `field_sectionQuote_quoteSource`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('1', '8', 'en', '<p><strong>\r\n	I enjoy&nbsp;projects where I can make a real difference.&nbsp;For example, I regularly help:</strong></p><ul>\r\n	<li>Businesses whose existing website doesn’t reflect their current character and doesn’t attract their ideal clients.&nbsp;</li>	<li>Charities that need to effectively communicate their work to attract supporters and donations.</li>	<li>Organisations whose&nbsp;site isn’t optimised for handheld devices and who&nbsp;are loosing out on a big chunk of visitors and potential customers.</li>	<li>Shops that need a great online store that generates sales and happy customers.</li>	<li>New businesses which need their first&nbsp;website&nbsp;to attract clients and supporters.</li>	<li>An agency or organisation that needs someone to work with their team to build a user-friendly, robust, and high-quality front-end code&nbsp;base for a big, complex site.</li></ul>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2014-05-26 15:11:47', '2014-10-14 13:42:56', '1ccef3c7-de30-4d7b-80f8-f377bb420622');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_sectionCodeSnippet_codeSnippet`, `field_sectionImage_imageCaption`, `field_sectionImage_imageAlt`, `field_sectionCodeSnippet_codeType`, `field_sectionImage_remoteImage`, `field_sectionQuote_quoteText`, `field_sectionQuote_quoteSource`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('18', '25', 'en', NULL, 'How I Got Here', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2014-05-26 19:32:19', '2014-10-14 12:53:04', '078dea3a-8f46-469f-9960-727f9ec0b9ac');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_sectionCodeSnippet_codeSnippet`, `field_sectionImage_imageCaption`, `field_sectionImage_imageAlt`, `field_sectionCodeSnippet_codeType`, `field_sectionImage_remoteImage`, `field_sectionQuote_quoteText`, `field_sectionQuote_quoteSource`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('19', '26', 'en', '<p>\r\n	I have always enjoyed using computers and the internet and&nbsp;remember the many hours I would spent listening to the shrieking of a 56k modem whilst&nbsp;patiently waiting for a website&nbsp;to load bit by bit by bit...</p><p>\r\n	In 2009, I had my first peek behind the scenes of making websites whilst working for&nbsp;<a href=\"http://www.spana.org\">SPANA</a> as a fundraiser. I was hooked straight away and&nbsp;completed a web development&nbsp;course.&nbsp;</p><p>Fast forward 5 years and I&nbsp;have been involved in over <a href=\"{entry:7:url}\">35 projects</a>, working&nbsp;with many&nbsp;agencies, organisations and&nbsp;individuals.</p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2014-05-26 19:32:19', '2014-10-14 12:53:04', '0ecc655a-65f9-437e-bfd2-5777037b6e2f');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_sectionCodeSnippet_codeSnippet`, `field_sectionImage_imageCaption`, `field_sectionImage_imageAlt`, `field_sectionCodeSnippet_codeType`, `field_sectionImage_remoteImage`, `field_sectionQuote_quoteText`, `field_sectionQuote_quoteSource`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('20', '27', 'en', NULL, 'Trade Tools', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2014-05-26 19:32:19', '2014-10-14 12:53:04', 'cecd8c51-64ce-4f3a-818f-09f4973d13e3');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_sectionCodeSnippet_codeSnippet`, `field_sectionImage_imageCaption`, `field_sectionImage_imageAlt`, `field_sectionCodeSnippet_codeType`, `field_sectionImage_remoteImage`, `field_sectionQuote_quoteText`, `field_sectionQuote_quoteSource`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('21', '28', 'en', '<ul>\r\n	<li>Pen and paper for planning</li>	<li><a href=\"http://www.bohemiancoding.com/sketch/\">Sketch</a> for prototypes,&nbsp;style tiles, and site assets management</li>	<li><a href=\"http://www.invisionapp.com/\">InVision</a> for presenting&nbsp;concepts and interactive prototypes</li>	<li><a href=\"http://www.sublimetext.com\">Sublime Text</a> for coding</li>	<li><a href=\"http://mixture.io/\">Mixture</a> for prepocessing,&nbsp;compiling and local testing</li>	<li>Git and&nbsp;<a href=\"http://www.git-tower.com/\">Tower</a> for version control</li>	<li><a href=\"http://buildwithcraft.com/\">Craft</a>,&nbsp;<a href=\"http://expressionengine.com/\">Expression Engine</a>,&nbsp;<a href=\"http://statamic.com/\">Statamic</a> and&nbsp;<a href=\"http://grabaperch.com/\">Perch</a> as CMS of choice</li><li><a href=\"http://www.browserstack.com/\">BrowserStack</a> for browser and device testing&nbsp;</li>	<li><a href=\"http://www.campaignmonitor.com/\">Campaign Monitor</a> and <a href=\"http://mailchimp.com/\">MailChimp</a> for email marketing</li>	<li><a href=\"https://basecamp.com/\">Basecamp</a>,&nbsp;<a href=\"https://redbooth.com/\">Redbooth</a> and Skype for project management and collaboration</li></ul>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2014-05-26 19:32:19', '2014-10-14 12:53:04', '22e5de7f-74dd-4f37-afb9-853105d31a66');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_sectionCodeSnippet_codeSnippet`, `field_sectionImage_imageCaption`, `field_sectionImage_imageAlt`, `field_sectionCodeSnippet_codeType`, `field_sectionImage_remoteImage`, `field_sectionQuote_quoteText`, `field_sectionQuote_quoteSource`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('22', '30', 'en', '<p>\r\n	I am currently available for new projects from <strong>August onwards</strong>.</p><h2>Email</h2><p><a href=\"mailto:hello@katrinkerber.com\">hello@katrinkerber.com</a></p><h2>Phone</h2><p><a href=\"tel:+44 1730 301 302\">01730 301 302</a></p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2014-05-26 19:39:51', '2014-08-05 09:34:36', 'e6912133-3748-446c-9561-1787eb28ff32');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_sectionCodeSnippet_codeSnippet`, `field_sectionImage_imageCaption`, `field_sectionImage_imageAlt`, `field_sectionCodeSnippet_codeType`, `field_sectionImage_remoteImage`, `field_sectionQuote_quoteText`, `field_sectionQuote_quoteSource`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('25', '33', 'en', NULL, 'Ethical Guidelines', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2014-05-26 20:47:34', '2014-10-14 12:53:04', '77b74f24-8507-4203-8948-8b69735bed75');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_sectionCodeSnippet_codeSnippet`, `field_sectionImage_imageCaption`, `field_sectionImage_imageAlt`, `field_sectionCodeSnippet_codeType`, `field_sectionImage_remoteImage`, `field_sectionQuote_quoteText`, `field_sectionQuote_quoteSource`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('26', '34', 'en', '<p>I try to be conscious about the consequences of my actions and the impact they can&nbsp;have on people, animals and the environment.&nbsp;I will never knowingly work for individuals or organisations who support or actively encourage harming people, animals or the planet and&nbsp;want to do my bit by focusing on working for good causes whenever I can.</p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2014-05-26 20:47:34', '2014-10-14 12:53:04', 'cf7c7ca2-a1b2-48d5-bc36-1fd3b904898e');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_sectionCodeSnippet_codeSnippet`, `field_sectionImage_imageCaption`, `field_sectionImage_imageAlt`, `field_sectionCodeSnippet_codeType`, `field_sectionImage_remoteImage`, `field_sectionQuote_quoteText`, `field_sectionQuote_quoteSource`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('29', '61', 'en', '<p>Thanks to some magic post sell-out tickets I was able to attend and thoroughly enjoy the <a href=\"http://responsiveconf.com/2013/\">first Responsive Day Out conference</a> last year. When the news broke that another one would be held this year, I naturally made sure I got my ticket the moment they went on sale.</p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2014-07-22 10:45:07', '2014-07-22 12:21:12', 'ffb3dfb8-cf99-4279-a891-a35f2d607fb9');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_sectionCodeSnippet_codeSnippet`, `field_sectionImage_imageCaption`, `field_sectionImage_imageAlt`, `field_sectionCodeSnippet_codeType`, `field_sectionImage_remoteImage`, `field_sectionQuote_quoteText`, `field_sectionQuote_quoteSource`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('31', '63', 'en', '<p>\r\n	It turned out to be another brilliant event. Jeremy Keith is a fantastic host and the three 20-minute talks followed by a short panel discussion are the perfect set-up. All the talks where of stellar quality and my mind was positively buzzing at the end.</p><p>\r\n	One notion that kept resonating the most with me throughout the day was how the challenge of building websites for uncountable devices and screen sizes is bringing us back to the most important basics: focussing on clear accessible content and user needs.</p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2014-07-22 11:03:03', '2014-07-22 12:21:12', 'fbc31f5e-025b-4f3c-bbdf-b7be059b4b70');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_sectionCodeSnippet_codeSnippet`, `field_sectionImage_imageCaption`, `field_sectionImage_imageAlt`, `field_sectionCodeSnippet_codeType`, `field_sectionImage_remoteImage`, `field_sectionQuote_quoteText`, `field_sectionQuote_quoteSource`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('32', '66', 'en', NULL, 'The Core Model', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2014-07-22 11:06:39', '2014-07-22 12:21:12', 'abfa2477-6973-496c-878f-56782aae0c30');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_sectionCodeSnippet_codeSnippet`, `field_sectionImage_imageCaption`, `field_sectionImage_imageAlt`, `field_sectionCodeSnippet_codeType`, `field_sectionImage_remoteImage`, `field_sectionQuote_quoteText`, `field_sectionQuote_quoteSource`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('33', '67', 'en', '<p>Ida Aalen talked about the core model and showed how it was used for the build of the&nbsp;<a href=\"https://kreftforeningen.no/\">Norwegian Cancer Society\'s website</a>.</p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2014-07-22 11:06:39', '2014-07-22 12:21:12', 'ebf522f5-8fb5-4217-81d3-6622824d0db8');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_sectionCodeSnippet_codeSnippet`, `field_sectionImage_imageCaption`, `field_sectionImage_imageAlt`, `field_sectionCodeSnippet_codeType`, `field_sectionImage_remoteImage`, `field_sectionQuote_quoteText`, `field_sectionQuote_quoteSource`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('34', '68', 'en', NULL, NULL, NULL, '<p>Photo: <a href=\"https://www.flickr.com/photos/marcthiele/\">Marc Thiele</a></p>', 'Three of the speakers and Jeremy Keith sitting on stage during one of the panel discussions', NULL, 'https://farm4.staticflickr.com/3868/14524999285_385890d100_c.jpg', NULL, NULL, '2014-07-22 11:07:36', '2014-07-22 12:21:12', 'f65e9740-18d3-43ba-aa9b-de274d741d07');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_sectionCodeSnippet_codeSnippet`, `field_sectionImage_imageCaption`, `field_sectionImage_imageAlt`, `field_sectionCodeSnippet_codeType`, `field_sectionImage_remoteImage`, `field_sectionQuote_quoteText`, `field_sectionQuote_quoteSource`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('35', '69', 'en', NULL, NULL, NULL, '', 'An example work sheet for the core model', NULL, '', NULL, NULL, '2014-07-22 11:07:48', '2014-07-22 12:21:12', '2cee603c-8db4-4690-a9c3-ee01af4ed58f');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_sectionCodeSnippet_codeSnippet`, `field_sectionImage_imageCaption`, `field_sectionImage_imageAlt`, `field_sectionCodeSnippet_codeType`, `field_sectionImage_remoteImage`, `field_sectionQuote_quoteText`, `field_sectionQuote_quoteSource`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('36', '70', 'en', '<p>By establishing key user needs, objectives and clear paths, this model boils content down to its most important core. Not only does this help create a site that effectively&nbsp;fulfils&nbsp;user and business goals, it also helps to get away from the misguided idea that the home page is the&nbsp;most important part of a site. The majority of users don\'t visit a site to look at the home page (many will never see it). Instead, they are looking for (and hopefully find) that piece of core content that meets their needs. I also love how this model adopts the \'mobile first\' mindset and asks to structure the established core content with a small screen in mind. This focus and meticulous upfront planning also helps to prevent falling into the \'Tetris&nbsp;trap\' when it comes to designing the layout of content for larger screens.</p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2014-07-22 11:09:00', '2014-07-22 12:21:12', '28c11581-2600-4542-a062-08e29ad783c1');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_sectionCodeSnippet_codeSnippet`, `field_sectionImage_imageCaption`, `field_sectionImage_imageAlt`, `field_sectionCodeSnippet_codeType`, `field_sectionImage_remoteImage`, `field_sectionQuote_quoteText`, `field_sectionQuote_quoteSource`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('37', '71', 'en', NULL, 'Death To Competing Content', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2014-07-22 11:09:00', '2014-07-22 12:21:12', '422a8950-7c17-418c-aad2-5ff484919725');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_sectionCodeSnippet_codeSnippet`, `field_sectionImage_imageCaption`, `field_sectionImage_imageAlt`, `field_sectionCodeSnippet_codeType`, `field_sectionImage_remoteImage`, `field_sectionQuote_quoteText`, `field_sectionQuote_quoteSource`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('38', '72', 'en', '<p>\r\n	Oliver Reichenstein\'s talk neatly picked up on that notion, too. He talked about the habit of filling screens with boxes of competing content, which results in a muddled message and confusing content flow.</p><p>\r\n	There are so many sites that make that one thing that they are built for - consumption of content - so incredibly frustrating. News sites surround their articles with convoluted fluff (animated images, really?!) and punching yourself in the face is less painful than looking for an answer on WikiAnswers. So, Oliver\'s call to stop putting unrelated content next to each other and instead adopt a container model that follows a logical vertical structure struck so many chords with me, my mind kept loudly shouting \'<strong>Yes! This! Please, everyone, listen to this man!</strong>\'.</p><p>\r\n	We were urged to move away from \'How do I fill in this box?\' to \'How do I provide and present the best quality content?\'.</p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2014-07-22 11:11:01', '2014-07-22 12:21:12', '4a5b2929-5ca0-4f70-88dd-ebab556a9b1c');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_sectionCodeSnippet_codeSnippet`, `field_sectionImage_imageCaption`, `field_sectionImage_imageAlt`, `field_sectionCodeSnippet_codeType`, `field_sectionImage_remoteImage`, `field_sectionQuote_quoteText`, `field_sectionQuote_quoteSource`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('39', '73', 'en', NULL, 'Let’s Make This Happen', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2014-07-22 11:11:01', '2014-07-22 12:21:12', 'dd8af326-4612-4e1b-9e5d-2e3cb0d85bc8');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_sectionCodeSnippet_codeSnippet`, `field_sectionImage_imageCaption`, `field_sectionImage_imageAlt`, `field_sectionCodeSnippet_codeType`, `field_sectionImage_remoteImage`, `field_sectionQuote_quoteText`, `field_sectionQuote_quoteSource`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('40', '74', 'en', '<p>Next to my excited agreement, I kept having creeping doubts about how to keep pushing this positive change forward. I frequently&nbsp;observe&nbsp;a&nbsp;herd-like mentality when it comes to applying solutions. Trying to convince designers or clients that a slideshow isn\'t an effective content presentation model is often still very hard work. And don\'t get me started on that three-line menu icon! The \'but everyone else is doing it\' notion is often hard to counter unless you have statistics and use cases that prove other ways are better. Because of these challenges, I hugely admire the work of people like Ida and Oliver, who get their clients to jump on board and help to forge a better path (just look at the <a href=\"http://next.theguardian.com/\">new Guardian site</a> - it\'s a beauty!).</p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2014-07-22 11:11:01', '2014-07-22 12:21:12', 'a29298d2-7beb-478e-8f4c-0fbc7216596b');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_sectionCodeSnippet_codeSnippet`, `field_sectionImage_imageCaption`, `field_sectionImage_imageAlt`, `field_sectionCodeSnippet_codeType`, `field_sectionImage_remoteImage`, `field_sectionQuote_quoteText`, `field_sectionQuote_quoteSource`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('41', '75', 'en', NULL, 'The Talks And Resources', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2014-07-22 11:11:01', '2014-07-22 12:21:12', '973fc0da-18af-4ff2-aa3e-1cc1764a45fe');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_sectionCodeSnippet_codeSnippet`, `field_sectionImage_imageCaption`, `field_sectionImage_imageAlt`, `field_sectionCodeSnippet_codeType`, `field_sectionImage_remoteImage`, `field_sectionQuote_quoteText`, `field_sectionQuote_quoteSource`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('42', '76', 'en', '<p>I\'ve only highlighted two speakers, but all ten of them were absolutely amazing and their talks were choc-a-bloc with great information. So I urge you to grab a mug of your favourite beverage, get comfy and <a href=\"http://huffduffer.com/adactio/tags/responsiveconf2?page=2\">listen to them all</a> together with the slides and resources:</p><ul> <li>Stephen Hay: \'<a href=\"https://speakerdeck.com/stephenhay/sculpting-text\">Sculpting Text</a>\'</li><li>Sally Jenkinson \'<a href=\"http://www.slideshare.net/sallyjenkinson/more-than-media-queries-responsive-day-out-2\">More Than Media Queries</a>\'</li><li>Ida Aalen: \'<a href=\"http://iallenkelhet.no/2014/06/27/responsive-day-out-2-slides-and-resources-from-my-talk-on-the-norwegian-cancer-society/\">The Core Model</a>\' and her amazing <a href=\"http://iallenkelhet.no/2014/06/27/notes-from-responsive-day-out-2-june-27th-in-brighton\">detailed notes from the conference</a></li><li>Rachel Andrew: \'<a href=\"https://speakerdeck.com/rachelandrew/css-grid-layout\">CSS Grid Layout</a>\'</li><li>Dan Donald: \'<a href=\"https://speakerdeck.com/hereinthehive/designing-evolution-elements\">Designing Evolution: Elements</a>\'</li><li>Inayaili de León Persson: \'<a href=\"https://speakerdeck.com/yaili/realistic-responsive-design\">Realistic responsive design</a>\'</li><li>Oliver Reichenstein didn\'t have any slides, but <a href=\"http://marcthiele.com/\">Marc Thiele</a> took some <a href=\"https://www.flickr.com/photos/marcthiele/14339046999/in/set-72157645354768166/\">amazing photos</a> which come close to encapsulating the atmosphere and energy of this talk</li><li>Kirsty Burgoine: \'<a href=\"http://www.slideshare.net/kirstyburgoine/question-of-deliverables-and-other-such-stuff\">Question of deliverables and other such stuff</a>\'</li><li>Stephanie Rieger: \'<a href=\"http://www.slideshare.net/yiibu/the-future-of-mediaqueries\">The future of media queries?</a>\'</li><li>Ethan Marcotte\'s slides aren\'t available yet, but there some more of Marc\'s <a href=\"https://www.flickr.com/photos/marcthiele/14339039669/in/set-72157645354768166\">photos of the talk</a></li></ul>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2014-07-22 11:11:01', '2014-07-22 12:21:12', 'c63eb3b1-e2e0-4c98-a326-29e2ae28d97c');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_sectionCodeSnippet_codeSnippet`, `field_sectionImage_imageCaption`, `field_sectionImage_imageAlt`, `field_sectionCodeSnippet_codeType`, `field_sectionImage_remoteImage`, `field_sectionQuote_quoteText`, `field_sectionQuote_quoteSource`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('43', '79', 'en', '<p>I made my first baby steps into building fully responsive sites last year, when I was re-doing this site.</p><p>Before I started, I was mulling over the options of making the navigation as accessible and usable as possible for mobile users. It was the time when the technique of hiding the menu behind a line icon became more and more the go-to solution. But something irked me about this. It just didn\'t feel quite right. It was as if we were assuming that users knew what the icon meant and what it does. Debating the effectiveness, usability and when this technique is a good (or the best) approach is a different story for another day.</p><p>All I knew was that I wanted to take a leaf out of the book of native apps and use a solution that they were using very effectively:</p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2014-07-22 12:02:55', '2014-08-05 09:15:03', 'fecf7f11-56ae-43eb-8c4c-1701b8abcb9e');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_sectionCodeSnippet_codeSnippet`, `field_sectionImage_imageCaption`, `field_sectionImage_imageAlt`, `field_sectionCodeSnippet_codeType`, `field_sectionImage_remoteImage`, `field_sectionQuote_quoteText`, `field_sectionQuote_quoteSource`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('44', '80', 'en', NULL, NULL, NULL, '<p>A bottom-fixed menu.</p>', 'Screenshot of the Twitter app on a mobile', NULL, '', NULL, NULL, '2014-07-22 12:02:55', '2014-08-05 09:15:03', 'b5132f58-a9e9-4e2e-b7b3-0804c16f0d73');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_sectionCodeSnippet_codeSnippet`, `field_sectionImage_imageCaption`, `field_sectionImage_imageAlt`, `field_sectionCodeSnippet_codeType`, `field_sectionImage_remoteImage`, `field_sectionQuote_quoteText`, `field_sectionQuote_quoteSource`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('45', '81', 'en', NULL, 'Box Of Chocolates', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2014-07-22 12:02:55', '2014-08-05 09:15:03', '4b20fbec-8c9b-4d96-b68e-51d27eabc57a');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_sectionCodeSnippet_codeSnippet`, `field_sectionImage_imageCaption`, `field_sectionImage_imageAlt`, `field_sectionCodeSnippet_codeType`, `field_sectionImage_remoteImage`, `field_sectionQuote_quoteText`, `field_sectionQuote_quoteSource`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('46', '82', 'en', '<p>I felt that this was one of the cleanest and most user-friendly mobile navigation solutions out there. I couldn\'t understand why it wasn\'t being shouted from the roof-tops.</p><p>That was until I dove into the subject and discovered that <a href=\"http://bradfrostweb.com/blog/mobile/fixed-position/\">support for \'position: fixed\' is atrocious across mobiles</a>. </p><p>Never in my wildest dreams did I imagine that such a basic CSS feature would not be fully supported. The web is like a box of chocolates.</p><p>But before I could cry into my pillow for too long, I saw that support picked up and it was <a href=\"http://caniuse.com/#feat=css-fixed\">working well in newer OS</a>. So, following the progressive enhancement approach, I was determined to implement the bottom-fixed menu for systems that could handle it. Older ones would get the default inline navigation in the header.</p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2014-07-22 12:02:55', '2014-08-05 09:15:03', '083c1f94-d819-4547-98c7-a7b3f007ab84');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_sectionCodeSnippet_codeSnippet`, `field_sectionImage_imageCaption`, `field_sectionImage_imageAlt`, `field_sectionCodeSnippet_codeType`, `field_sectionImage_remoteImage`, `field_sectionQuote_quoteText`, `field_sectionQuote_quoteSource`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('48', '84', 'en', '<p>Since \r\n	<code style=\"line-height: 1.6em;\">position: fixed</code> works well in iOS 5+ and Android 3+, I use this snippet of JavaScript below my Modernizr script, which is called in the head:</p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2014-07-22 12:02:55', '2014-08-05 09:15:03', 'b2e279db-6114-4a18-b935-ecc8b5191190');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_sectionCodeSnippet_codeSnippet`, `field_sectionImage_imageCaption`, `field_sectionImage_imageAlt`, `field_sectionCodeSnippet_codeType`, `field_sectionImage_remoteImage`, `field_sectionQuote_quoteText`, `field_sectionQuote_quoteSource`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('49', '86', 'en', NULL, NULL, 'var ua = navigator.userAgent;\r\n// iOS 5+\r\nif( /(iPhone|iPod)/i.test(ua) ) { \r\n    if(/OS [2-4]_\\d(_\\d)? like Mac OS X/i.test(ua)) {  \r\n        // iOS 2-4\r\n    } else if(/CPU like Mac OS X/i.test(ua)) {\r\n        // iOS 1\r\n    } else {\r\n      document.getElementsByTagName(\'html\')[0].className += \'positionfixed\';\r\n    }\r\n}\r\n// Android 3+\r\nif( ua.indexOf(\"Android\") >= 0 )\r\n{\r\n  var androidversion = parseFloat(ua.slice(ua.indexOf(\"Android\")+8)); \r\n  if (androidversion >= 3)\r\n  {\r\n      document.getElementsByTagName(\'html\')[0].className += \'positionfixed\';\r\n  }\r\n}', NULL, NULL, 'language-javascript', NULL, NULL, NULL, '2014-07-22 12:12:27', '2014-08-05 09:15:03', '7862247f-c53b-427a-b4a8-f5913170d4ce');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_sectionCodeSnippet_codeSnippet`, `field_sectionImage_imageCaption`, `field_sectionImage_imageAlt`, `field_sectionCodeSnippet_codeType`, `field_sectionImage_remoteImage`, `field_sectionQuote_quoteText`, `field_sectionQuote_quoteSource`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('50', '87', 'en', '<p>User-agent string sniffing isn\'t a great idea most of the time and it usually doesn\'t sit right with me. But for this I am willing to make an exception.</p><p>This script looks into the past and determines if an old iOS or Android version is in use. If it is not, we (currently) know that the feature is supported. </p><p>Of course, there is always the chance that iOS or Android suddenly drop support again in the future. Then I shall curse, raise my fists and cross that bridge when it comes to it.</p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2014-07-22 12:12:27', '2014-08-05 09:15:03', '8f010c9e-77e0-4b85-a603-d3826c38f986');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_sectionCodeSnippet_codeSnippet`, `field_sectionImage_imageCaption`, `field_sectionImage_imageAlt`, `field_sectionCodeSnippet_codeType`, `field_sectionImage_remoteImage`, `field_sectionQuote_quoteText`, `field_sectionQuote_quoteSource`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('51', '88', 'en', NULL, 'A Little Bit of CSS', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2014-07-22 12:12:27', '2014-08-05 09:15:03', '286142d0-d325-478c-98ae-f8363605f6c8');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_sectionCodeSnippet_codeSnippet`, `field_sectionImage_imageCaption`, `field_sectionImage_imageAlt`, `field_sectionCodeSnippet_codeType`, `field_sectionImage_remoteImage`, `field_sectionQuote_quoteText`, `field_sectionQuote_quoteSource`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('52', '89', 'en', '<p>\r\n	Based on the above, I use this bit of CSS to fix the menu to the bottom of the screen on devices which have a width of less than <code>36em</code>:</p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2014-07-22 12:12:27', '2014-08-05 09:15:03', '0e9c6138-44d9-480c-9efd-722e36a9cd72');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_sectionCodeSnippet_codeSnippet`, `field_sectionImage_imageCaption`, `field_sectionImage_imageAlt`, `field_sectionCodeSnippet_codeType`, `field_sectionImage_remoteImage`, `field_sectionQuote_quoteText`, `field_sectionQuote_quoteSource`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('53', '90', 'en', NULL, NULL, '@media only screen and (max-device-width: 36em) {\r\n  .positionfixed nav {\r\n    position: fixed;\r\n    bottom: 0;\r\n    z-index: 999;\r\n  }\r\n}', NULL, NULL, 'language-css', NULL, NULL, NULL, '2014-07-22 12:12:27', '2014-08-05 09:15:03', '6e179907-a3f5-4784-9727-2fa29968b3aa');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_sectionCodeSnippet_codeSnippet`, `field_sectionImage_imageCaption`, `field_sectionImage_imageAlt`, `field_sectionCodeSnippet_codeType`, `field_sectionImage_remoteImage`, `field_sectionQuote_quoteText`, `field_sectionQuote_quoteSource`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('54', '91', 'en', NULL, 'The Result', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2014-07-22 12:12:27', '2014-08-05 09:15:03', '4d40b0a4-6811-4980-b0ce-1a98db171074');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_sectionCodeSnippet_codeSnippet`, `field_sectionImage_imageCaption`, `field_sectionImage_imageAlt`, `field_sectionCodeSnippet_codeType`, `field_sectionImage_remoteImage`, `field_sectionQuote_quoteText`, `field_sectionQuote_quoteSource`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('55', '92', 'en', NULL, NULL, NULL, '', 'Screenshot of the bottom-fixed menu I used to use on my site', NULL, '', NULL, NULL, '2014-07-22 12:12:27', '2014-08-05 09:15:03', '85cc6286-39d4-4309-9ae4-e723857c704b');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_sectionCodeSnippet_codeSnippet`, `field_sectionImage_imageCaption`, `field_sectionImage_imageAlt`, `field_sectionCodeSnippet_codeType`, `field_sectionImage_remoteImage`, `field_sectionQuote_quoteText`, `field_sectionQuote_quoteSource`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('56', '93', 'en', '<p>This approach is not perfect, but nothing hardly ever is around these web parts. For now, I am happy using this to give that little bit of extra to users whose devices can handle it.</p>', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '2014-07-22 12:12:27', '2014-08-05 09:15:03', 'c7a761f2-a3a6-44f8-9c05-b25fa140b3b2');
