INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_codeSnippet_codeSnippet`, `field_image_imageCaption`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('1', '8', 'en', '<p>\r\n	I specialise in front-end development and CMS integration. I work with&nbsp;organisations,&nbsp;agencies and&nbsp;designers to&nbsp;transform ideas and designs into user-friendly,&nbsp;effective and maintainable sites.</p>', NULL, NULL, NULL, '2014-05-26 15:11:47', '2014-06-25 23:18:53', '1ccef3c7-de30-4d7b-80f8-f377bb420622');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_codeSnippet_codeSnippet`, `field_image_imageCaption`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('18', '25', 'en', NULL, 'How I Got Here', NULL, NULL, '2014-05-26 19:32:19', '2014-05-26 20:50:38', '078dea3a-8f46-469f-9960-727f9ec0b9ac');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_codeSnippet_codeSnippet`, `field_image_imageCaption`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('19', '26', 'en', '<p>\r\n	I have always enjoyed using computers and the internet and&nbsp;remember well the many hours spent listening to the shrieking of a 56k modem whilst&nbsp;patiently waiting for a website&nbsp;to load bit by bit by bit...</p><p>\r\n	In 2009 I had my first peek behind the scenes of making websites whilst working&nbsp;<a href=\"http://www.spana.org\">SPANA</a> as a fundraiser. I was hooked straight away and&nbsp;completed a web development&nbsp;course.&nbsp;Over the last five years&nbsp;I have&nbsp;worked with&nbsp;agencies, organisations and&nbsp;individuals and&nbsp;have been involved in over 35 projects.</p>', NULL, NULL, NULL, '2014-05-26 19:32:19', '2014-05-26 20:50:38', '0ecc655a-65f9-437e-bfd2-5777037b6e2f');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_codeSnippet_codeSnippet`, `field_image_imageCaption`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('20', '27', 'en', NULL, 'Trade Tools', NULL, NULL, '2014-05-26 19:32:19', '2014-05-26 20:50:38', 'cecd8c51-64ce-4f3a-818f-09f4973d13e3');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_codeSnippet_codeSnippet`, `field_image_imageCaption`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('21', '28', 'en', '<ul>\r\n	\r\n<li>Pen and paper for planning</li>	\r\n<li><a href=\"http://www.bohemiancoding.com/sketch/\">Sketch</a> for prototypes,&nbsp;style tiles, and site assets management</li>	\r\n<li><a href=\"http://www.invisionapp.com/\">InVision</a> for presenting concepts and click-able prototypes</li>	\r\n<li><a href=\"http://www.sublimetext.com\">Sublime Text 2</a> for coding</li>	\r\n<li>Mixture for prepocessing,&nbsp;compiling and local testing</li>	\r\n<li>Git and Tower for version control</li>	\r\n<li>BrowserStack for testing&nbsp;</li>	\r\n<li>Craft,&nbsp;<a href=\"http://expressionengine.com/\">Expression Engine</a>,&nbsp;<a href=\"http://statamic.com/\">Statamic</a> and Perch as CMS of choice</li>	\r\n<li><a href=\"http://www.campaignmonitor.com/\">Campaign Monitor</a> and <a href=\"http://mailchimp.com/\">MailChimp</a> for email marketing</li></ul>', NULL, NULL, NULL, '2014-05-26 19:32:19', '2014-05-26 20:50:38', '22e5de7f-74dd-4f37-afb9-853105d31a66');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_codeSnippet_codeSnippet`, `field_image_imageCaption`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('22', '30', 'en', '<p>\r\n	I\'d love to hear about your project and chat about how I could help.&nbsp;</p><p>\r\n	I am available from July. My day rate is £245&nbsp;and project costs usually start from around £2,000.</p><p>\r\n	If that sounds good to you, do get in touch and&nbsp;let me know about your requirements, budget and timelines.</p>', NULL, NULL, NULL, '2014-05-26 19:39:51', '2014-05-26 21:08:28', 'e6912133-3748-446c-9561-1787eb28ff32');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_codeSnippet_codeSnippet`, `field_image_imageCaption`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('23', '31', 'en', NULL, 'What I Do', NULL, NULL, '2014-05-26 20:39:25', '2014-05-26 20:50:38', '782b7692-6170-4609-8623-41e406c03e30');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_codeSnippet_codeSnippet`, `field_image_imageCaption`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('24', '32', 'en', '<h3>Planning and Prototyping</h3><p>\r\n	I have a passion for user-centric design and detailed planning. I can work with you to establish the right site&nbsp;structure and content hierarchy by creating site maps, wireframes and interactive prototypes. I usually plan&nbsp;\'mobile first\' which ensures a focus on the essentials.</p>', NULL, NULL, NULL, '2014-05-26 20:39:25', '2014-05-26 20:50:38', 'f1477d3f-fb22-4c2b-9a72-dbac3dd51015');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_codeSnippet_codeSnippet`, `field_image_imageCaption`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('25', '33', 'en', NULL, 'Ethical Guidelines', NULL, NULL, '2014-05-26 20:47:34', '2014-05-26 20:50:38', '77b74f24-8507-4203-8948-8b69735bed75');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_codeSnippet_codeSnippet`, `field_image_imageCaption`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('26', '34', 'en', '<p>I try to be conscious about the consequences of my actions and the impact they can&nbsp;have on people, animals and the environment.&nbsp;I will never knowingly work for individuals or organisations who support or actively encourage harming people, animals or the planet and&nbsp;want to do my bit by focusing on working for good causes whenever I can.</p>', NULL, NULL, NULL, '2014-05-26 20:47:34', '2014-05-26 20:50:38', 'cf7c7ca2-a1b2-48d5-bc36-1fd3b904898e');
INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_codeSnippet_codeSnippet`, `field_image_imageCaption`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('27', '37', 'en', '<p>I\'d love to hear about your project and chat about how I could help.</p>', NULL, NULL, NULL, '2014-05-26 21:08:28', '2014-05-26 21:08:28', '9c3bab6f-a672-42e2-abad-d159d7fd7810');
