INSERT INTO `craft_matrixcontent_pagecontentsections` (`id`, `elementId`, `locale`, `field_sectionText_text`, `field_sectionTitle_sectionTitle`, `field_sectionCodeSnippet_codeSnippet`, `field_sectionImage_imageCaption`, `field_sectionImage_imageAlt`, `field_sectionCodeSnippet_codeType`, `field_sectionImage_remoteImage`, `dateCreated`, `dateUpdated`, `uid`) VALUES ('19', '26', 'en', '<p>\r\n	I have always enjoyed using computers and the internet and&nbsp;remember well the many hours spent listening to the shrieking of a 56k modem whilst&nbsp;patiently waiting for a website&nbsp;to load bit by bit by bit...</p><p>\r\n	In 2009 I had my first peek behind the scenes of making websites whilst working&nbsp;<a href=\"http://www.spana.org\">SPANA</a> as a fundraiser. I was hooked straight away and&nbsp;completed a web development&nbsp;course.&nbsp;</p><p>Since then I have&nbsp;worked with many&nbsp;agencies, organisations and&nbsp;individuals and&nbsp;have been involved in over <a href=\"{entry:7:url}\">35 projects</a>.</p>', NULL, NULL, NULL, NULL, NULL, NULL, '2014-05-26 19:32:19', '2014-07-22 10:17:57', '0ecc655a-65f9-437e-bfd2-5777037b6e2f');
